import React from 'react'
import BuyItemModal from './BuyItemModal.js'
import SellItemModal from './SellItemModal.js'
import LoanModal from './LoanModal.js'
import LocationModal from './LocationModal.js'
import PlayerBagModal from './PlayerBagModal.js'
import ItemLine from './ItemLine.js'
import ItemButtons from './ItemButtons.js'

export default function ItemsList(props) {

        let playerItems = props.playerItems;
        let funds = props.funds;
        let loan = props.loan;
        let day = props.currentDay;
        let location = props.currentLocation;
        let openBuyItemModal = props.openBuyItemModal;
        let openSellItemModal = props.openSellItemModal;
        let cancelButton = props.cancelButton;
        let setGameview = props.setGameview;
        let upDateGameview = props.upDateGameview;
        let doesPlayerHaveThis = props.doesPlayerHaveThis;
        let playerItemsQuantityMax = props.playerItemsQuantityMax;
        let currentDay = props.currentDay;

        if (props.currentDay > this.currentDay){this.items.map(function (item) {
                        let priceMin = item.minPrice;
                        let priceMax = item.maxPrice;
                        function currentItemPrice(priceMin, priceMax) {
                            return Math.floor(Math.random() * (priceMax - priceMin)) + priceMin;
                        };
                        const itemPrice = currentItemPrice(priceMin, priceMax);

                        console.log(doesPlayerHaveThis(item));
                        return <div className="itemLineContainer">
                            <ItemLine setGameview={setGameview} playerItemsQuantityMax={playerItemsQuantityMax} price={itemPrice} upDateGameview={upDateGameview} item={item} doesPlayerHaveThis={doesPlayerHaveThis} openBuyItemModal={openBuyItemModal} openSellItemModal={openSellItemModal} playerItems={playerItems} />
                            <ItemButtons currentDay={currentDay} setGameview={setGameview} openBuyItemModal={openBuyItemModal} openSellItemModal={openSellItemModal} item={item} price={itemPrice} playerItems={playerItems} playerItemsQuantityMax={playerItemsQuantityMax} doesPlayerHaveThis={doesPlayerHaveThis} />
                        </div>
                    })}
        
    }
