import React from 'react'

export default class GameView extends React.Component {
    constructor(props){
        super(props, context);
    
        this.state = {
            }
    
     
    }
    
    render() {
        let items = this.props.items;
        let funds = this.props.funds;
let loan = this.props.loan;
let day = this.props.currentDay;
let location = this.props.currentLocation;
    return ( <section>
        
    <section className="playerInfo">Funds: ${funds} | Loan: ${loan} | Day: ${day}/30</section>
    <section className="location">${location}</section>
    <section className="items">
{items.map(function (item) {
    let priceMin = item.minPrice;
    let priceMax = item.maxPrice;
    function currentItemPrice(priceMin, priceMax) {
        return Math.floor(Math.random() * (priceMax - priceMin)) + priceMin;
    };
    let itemPrice = currentItemPrice(priceMin, priceMax);

   return <div> 
       <div className="left" id="${item.name}" onClick="${this.props.openItemModal}">${item.quantity} ${item.name}</div>
    <div className="right">${itemPrice}
    <button className= "buy" onClick={this.props.buyItemModal} name="buy" value="Buy">Buy</button>
    <button className= {(this.props.playerItems.find(a => (a.name == item.name))) ? 'hidden' : 'sell'} onClick={this.props.sellItemModal} name="sell" value="Sell">Sell</button>
    </div>
    <div className="dotted"></div>
    </div>
  })}
    </section>
     <section className="gameButtons">
     <button className={(this.state.isPayLoanButtonActive) ? 'loan' : 'hidden'} type="submit" name="payLoan" value="Pay Loan">Pay Loan</button>
     <button className= "move" onClick={this.props.moveButton} name="move" value="Move">MOVE</button></section>
     <BuyItemModal isActive={this.state.isBuyItemModalActive} />
    <LoanModal isActive={this.state.isLoanModalActive} />
    <LocationModal isActive={this.state.isLocationModalActive} />
     </section>
        )
    }
}
